<?php

namespace Eleadtech\ProductAttachment\Block\Adminhtml\Pattachment;

class Edit extends \Magento\Backend\Block\Widget\Form\Container
{
    /**
     * Core registry
     *
     * @var \Magento\Framework\Registry
     */
    protected $_coreRegistry = null;

    /**
     * @param \Magento\Backend\Block\Widget\Context $context
     * @param \Magento\Framework\Registry $registry
     * @param array $data
     */
    public function __construct(
        \Magento\Backend\Block\Widget\Context $context,
        \Magento\Framework\Registry $registry,
        array $data = []
    ) {
        $this->_coreRegistry = $registry;
        parent::__construct($context, $data);
    }

    /**
     * Initialize pattachment edit block
     *
     * @return void
     */
    protected function _construct()
    {
        $this->_objectId = 'entity_id';
        $this->_blockGroup = 'Eleadtech_ProductAttachment';
        $this->_controller = 'adminhtml_pattachment';

        parent::_construct();

        $this->buttonList->update('save', 'label', __('Save Pattachment'));
        $this->removeButton("save");
        $this->removeButton("reset");
        $this->addButton(
            'reload',
            ['label' => __('Reload'), 'onclick' => 'setLocation(window.location.href)', 'class' => 'reset'],
            -1
        );
        $this->buttonList->update('delete', 'label', __('Delete Pattachment'));
    }

    /**
     * Retrieve text for header element depending on loaded post
     *
     * @return \Magento\Framework\Phrase
     */
    public function getHeaderText()
    {
        if ($this->_coreRegistry->registry('pattachment')->getId()) {
            return __("Edit Pattachment '%1'", $this->escapeHtml($this->_coreRegistry->registry('pattachment')->getTitle()));
        } else {
            return __('New Pattachment');
        }
    }

    /**
     * Getter of url for "Save and Continue" button
     * tab_id will be replaced by desired by JS later
     *
     * @return string
     */
    protected function _getSaveAndContinueUrl()
    {
        return $this->getUrl('productattachment/*/save', ['_current' => true, 'back' => 'edit', 'active_tab' => '{{tab_id}}']);
    }

    /**
     * Prepare layout
     *
     * @return \Magento\Framework\View\Element\AbstractBlock
     */
    protected function _prepareLayout()
    {
        $this->_formScripts[] = "
            function toggleEditor() {
                if (tinyMCE.getInstanceById('page_content') == null) {
                    tinyMCE.execCommand('mceAddControl', false, 'content');
                } else {
                    tinyMCE.execCommand('mceRemoveControl', false, 'content');
                }
            };
        ";
        return parent::_prepareLayout();
    }

}
